    @Test
    public void treeInsertionTest() {

        HashMap<Integer, Integer> hashMap = new HashMap<>();

       BinaryTree tree1 = new BinaryTree();
        tree1.setRoot(new BTNode(42));
        tree1.getRoot().setLeft(new BTNode(100));
        tree1.getRoot().setRight(new BTNode(200));
        tree1.getRoot().getLeft().setLeft(new BTNode(160));
        tree1.getRoot().getLeft().setRight(new BTNode(250));


        BinaryTree tree2 = new BinaryTree();
        tree2.setRoot(new BTNode(150));
        tree2.getRoot().setLeft(new BTNode(100));
        tree2.getRoot().setRight(new BTNode(250));
        tree2.getRoot().getLeft().setLeft(new BTNode(160));
        tree2.getRoot().getLeft().setRight(new BTNode(75));

        HashSet<Integer> result =new HashSet<>();
        result.add(160);
        result.add(100);
        result.add(250);

       HashSet  expected = hashMap.tree_intersection(tree1, tree2);

        assertEquals(expected, result);


    }
    @Test
    public void testLeftJoin(){
        HashMap<String,String> hashMap=new HashMap<>();
        HashMap<String , String> hashMap1 = new HashMap<>();
        hashMap1.set("diligent" , "employed");
        hashMap1.set("fond" , "enamored");
        hashMap1.set("guide" , "usher");
        hashMap1.set("outfit" , "garb");
        hashMap1.set("wrath" , "anger");

        HashMap<String , String> hashMap2 = new HashMap<>();
        hashMap2.set("diligent" , "idle");
        hashMap2.set("fond" , "averse");
        hashMap2.set("guide" , "follow");
        hashMap2.set("flow" , "jam");
        hashMap2.set("wrath" , "delight");
        String result = String.valueOf(hashMap.leftJoin(hashMap1,hashMap2));
        String expected = "[[diligent, employed, idle], [wrath, anger, delight], [fond, enamored, averse], [guide, usher, follow], [outfit, garb, null]]";
        assertEquals(expected, result);

    }

